import React, {Component} from 'react';
import './App.css';
import List from '../components/list/list';
import Input from '../components/input-element/input'

class App extends Component{
  state = {
    tasks: [
      {id: 0, status: "to do", task:"123456789123456789123456789"},
      {id: 1, status: "done", task:"task by state"},
    ],

    taskSavior : 'test'
  }

  //função para alterar 
  toggleStatus = (taskIndex) => {
    const tasks = [...this.state.tasks]
    const task = tasks[taskIndex]

    task.status === 'to do'? task.status = 'done' : task.status = 'to do'

    this.setState({tasks:tasks})

  }

  //função para deletar
  taskDelete = (taskIndex) => {
    const tasks = [...this.state.tasks];
    tasks.splice(taskIndex, 1);
    this.setState({tasks:tasks})
  }

  //função para salvar
  taskSave = () =>{
    //const task = 
    console.log('it"s working')
  }

  //função para digitar
  tipeEvent = (event) =>{
    this.setState({taskSavior : 'teste da função'})
  }


  render(){


    return(
      <div className='App'>
        <Input
          EventSave = {this.taskSave}

          value = {this.state.taskSavior}
          onChange = {this.tipeEvent}

        />
        <List
          tasks = {this.state.tasks}
          EventPositive = {this.toggleStatus}
          EventExclude = {this.taskDelete}
        />
      </div>
    )
  }
}

export default App;
