import React, {Component} from 'react';
import './App.css';
import List from '../components/list/list';

class App extends Component{
  state = {
    tasks: [
      {id: 0, status: "to do", task:"task by state"},
      {id: 1, status: "done", task:"task by state"},
    ]
  }

  funcaoTestDois = (taskIndex) => {
    const tasks = {...this.state.tasks}
    const task = tasks[taskIndex]

    task.status = 'done'

    console.log(task)
  //  task.status === 'to do'? task.status = 'done' : task.status = 'to do'

  //  this.setState({tasks:tasks})
  }

  taskDelete = (taskIndex) => {
    const tasks = [...this.state.tasks];
    tasks.splice(taskIndex, 1);
    this.setState({tasks:tasks})
  }

  render(){


    return(
      <div className='App'>
        <List
          tasks = {this.state.tasks}
          EventPositive = {this.funcaoTestDois}
          EventExclude = {this.taskDelete}
        />
      </div>
    )
  }
}

export default App;
